var mysql = require('mysql2');

var con = mysql.createConnection({
  host: "localhost",
  user: "abdalghany",
  password: "ohitsme",
  database:"atm"
});

module.exports = {
  fetchPasswordFromDb: function(userAccountNo, callback) {
    con.connect(function(err) {
      if (err) throw err;
      console.log("Connected!");
      con.query("SELECT customerpin FROM customers WHERE customeraccountno = ?", [userAccountNo], function(err, result, fields) {
        if (err) return callback(err, null);
        if (result && result.length > 0) {
          callback(null, result[0].customerpin);
        } else {
          callback(null, "User does not exist");
        }
      });
    });
  }
};
